import React, { useState } from 'react';
import CharacterDetails from './CharacterDetails';

const Dashboard = () => {
  const [characters, setCharacters] = useState([
    { id: 1, name: 'Spongebob Squarepants', description: 'Main character, a sponge who lives in a pineapple under the sea.', imageUrl: 'https://upload.wikimedia.org/wikipedia/en/3/3b/SpongeBob_SquarePants_character.svg' },
    { id: 2, name: 'Patrick Star', description: 'Spongebob\'s best friend, a starfish who is usually seen lounging under a rock.', imageUrl: 'https://upload.wikimedia.org/wikipedia/en/thumb/3/33/Patrick_Star.svg/800px-Patrick_Star.svg.png' },
    { id: 3, name: 'Squidward Tentacles', description: 'Spongebob\'s grumpy neighbor and co-worker at the Krusty Krab.', imageUrl: 'https://upload.wikimedia.org/wikipedia/en/7/79/Squidward_Tentacles_%28fair_use%29.svg' },
    { id: 4, name: 'Mr. Krabs', description: 'Owner of the Krusty Krab and Spongebob\'s boss.', imageUrl: 'data:image/webp;base64,' },
    { id: 5, name: 'Sandy Cheeks', description: 'A squirrel from Texas who is friends with Spongebob.', imageUrl: 'data:image/webp;base64,' },
    { id: 6, name: 'Gary the Snail', description: 'Spongebob\'s pet snail who meows like a cat.', imageUrl: 'https://images-wixmp-ed30a86b8c4ca887773594c2.wixmp.com/f/0c3d1125-9b26-4227-a5a6-5de7db9db5eb/dfe5pdk-a9431218-6e42-4dc9-a046-943f5b9a6b90.jpg/v1/fill/w_1032,h_774,q_70,strp/gary_the_snail__by_jtmx123_dfe5pdk-pre.jpg?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..2ZUO6L4YNv9dlAJTFMiUMRcNuzhjY4baxE_sc0moTwQ' },
  ]);

  const [editCharacter, setEditCharacter] = useState(null);

  const handleDelete = (id) => {
    setCharacters(characters.filter(character => character.id !== id));
  };

  const handleEdit = (id) => {
    setEditCharacter(id);
  };

  const handleSaveChanges = (id, newName, newDescription, newImageUrl) => {
    setCharacters(characters.map(character => {
      if (character.id === id) {
        return {
          ...character,
          name: newName,
          description: newDescription,
          imageUrl: newImageUrl,
        };
      }
      return character;
    }));
    setEditCharacter(null); // Exit edit mode after saving changes
  };

  return (
    <div className="dashboard">
      {characters.map(character => (
        <div key={character.id} className="character-portrait">
          <img src={character.imageUrl} alt={character.name} onClick={() => setEditCharacter(character.id)} />
          {editCharacter === character.id ? (
            <CharacterDetails
              character={character}
              onSaveChanges={handleSaveChanges}
              onDelete={() => handleDelete(character.id)}
            />
          ) : null}
        </div>
      ))}
    </div>
  );
};

export default Dashboard;
